import { useState } from "react";

type Props = {
  initialValue?: number;
};

export const CounterBy = ({ initialValue = 0 }: Props) => {
  const [counter, setCounter] = useState({ counter: initialValue, clicks: 0 });

  const handleClick = (amount: number) => {
    setCounter((prevValue) => ({
      ...prevValue,
      counter: prevValue.counter + amount,
      clicks: prevValue.clicks + amount,
    }));
  };

  return (
    <>
      <h1>Counter: {counter.counter}</h1>
      <h1>Clicks: {counter.clicks}</h1>

      <button onClick={() => handleClick(1)}>+1</button>
      <button onClick={() => handleClick(5)}>+5</button>
    </>
  );
};
