import { useState } from "react";

interface Props {
  onChange?: () => void;
}

export const useProduct = ({ onChange }: Props) => {
  const [counter, setCounter] = useState(0);

  const increaseBy = (value: number) => {
    setCounter((prev) => Math.max(prev + value, 0));

    onChange && onChange();
  };

  return { counter, increaseBy };
};
